@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+KR:wght@300;500;700&family=Permanent+Marker&display=swap');

/************************* color_plan ***************************/
$point_color_amber : #ffbf00;
$point_color_black : #000;
$main_color_flora_white : #FFFDF6;
$sub_color_free_speach_blue : #4136BF;
$color_white : #fff;

/************************* font_plan ***************************/
@mixin main_title() {
    font-size: 2.4rem;
    font-weight: 700;
}

@mixin sub_title() {
    font-size: 2rem;
    font-weight: 700;
}

$defalut_font_size : 1.6rem;
$small_font_size : 1.2rem;
$small_up_font_size : 1.4rem;

$font_noto_sans : 'Noto Sans KR',
sans-serif;
$font_perment_marker : 'Permanent Marker';

.color_gray {
    color: rgb(119, 119, 119);
}
/************************* gapï¼†padding ***************************/
@mixin gap($value) {
    margin: $value;
}

@mixin padding($value) {
    padding: $value;
}

/************************* background standard  ***************************/
@mixin background_contain {
    background-size: contain;
    background-repeat: no-repeat;
}

@mixin background_cover {
    background-size: cover;
    background-repeat: no-repeat;
}

/************************* item_size ***************************/
@mixin item_size ($width, $height) {
    width: $width;
    height: $height;
}

/************************* text_array ***************************/
@mixin text_array ($height) {
    text-align: center;
    line-height: $height;
}

/************************* center array  ***************************/
@mixin box_array {
    width: 1245px;
    margin: 0 auto;
}

/************************* defalut_settings ***************************/
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

li {
    list-style: none;
}

a {
    display: block;
    text-decoration: none;
    color: inherit;
}

html {
    font-size: 10px;
    background-color: $main_color_flora_white;
}

img {
    width: 100%;
    vertical-align: bottom;
}

.sub_title {
    @include sub_title;
    @include text_array(20px);
    margin: 20px 0;
    font-family: $font_perment_marker;
}

.btn {
    position: absolute;
    top: 50%;
    font-size: 50px;
    transform: translateY(-25%);
    z-index: 9999;
    cursor: pointer;
    transition: all 0.2s;
    user-select: none;

    &:hover {
        color: #ffbf00 !important;
    }
}

button {
    border: none;
    outline: none;
    cursor: pointer;
}

label {
    user-select: none;
}

@mixin blue_btn($font_size) {
    background-color: $sub_color_free_speach_blue;
    color: white;
    font-size: $font_size;
}

/********************** control_class ************************/
.none_on {
    display: none !important;
}

.block_on {
    display: block !important;
}

.full_down_on {
    width: 100%;
    height: 400px !important;
}

.visual_page_on {
    font-weight: 700;
}

.hover_on {
    opacity: 1 !important;
}

label {
    display: block;
    cursor: pointer;
}

select {
    outline: none;
}

input {
    display: none;
}
.label_style {
    font-size: $small_up_font_size;
}
.clicked {
    color: black !important;
}
.bgc_amber {
    background-color: $point_color_amber !important;
    color: white;
    font-weight: 700;
}


/************************* contents_product_buy ***************************/
.contents_product_buy {
    display: flex;
    @include box_array;
    margin-top: 60px;
    .product_view_area {
        width: 510px;
        margin-right: 225px;
        .style_code {
            overflow: hidden;
            margin-bottom: 15px;
            p {
                float: left;
                font-size: $defalut_font_size;
                font-family: $font_noto_sans;
                &:first-child {
                    margin-right: 15px;
                }
                &.code_number {
                    font-weight: 700;
                }
            }
        }
        .view_thumnail {
            position: relative;
            @include item_size(100%, 520px);
            overflow: hidden;
            margin-bottom: 15px;
            background-color: #f5f5f5;
            >div {
                @include item_size(100%, 520px);
                background-repeat: no-repeat;
            }
            &:hover {
                cursor: crosshair;
            }
            .view_img01 {
                position: absolute;
                top: 0; left: 0;
                opacity: 1;
                transition: all 0.2s;
                background-image: url(../dist/images/detail_page_img/CA23SSJAJL00358002_thumnail/CA23SSJAJL00358002-01.jpg);
                &:hover{
                    opacity: 0;
                }
            }
            .view_img02 {
                background-image: url(../dist/images/detail_page_img/CA23SSJAJL00358002_thumnail/CA23SSJAJL00358002-01_hover.jpg);
            }
        }
        .view_small_thumnail {
            display: flex;
            margin-bottom: 75px;
            .img_box {
                @include item_size(90px, 90px);
                margin-right: 15px;
                overflow: hidden;
                cursor: pointer;
                transition: all 0.2s;
                &:hover{
                    outline: 1px solid $sub_color_free_speach_blue;
                }
                img {
                    width: 100%;
                }
            }
            .src_img {
                display: none;
                @include item_size(90px, 90px);
            }
            .img_box05 {
                margin-right: 0;
            }
        }
        .product_related_area {
            .related_list {
                display: flex;
                margin-bottom: 15px;
            }
        }
        .recently_viewed_product_area {
            margin-bottom: 60px;
            .recently_list {
                display: flex;
            }
        }

        .title_text {
            @include sub_title;
            margin-bottom: 5px;
        }
        .small_product_img {
            margin-right: 15px;
            &:last-child {
                margin-right: 0;
            }
            .re_img_box {
                @include item_size(195px, 195px);
                overflow: hidden;
                img {
                    width: 100%; height: 100%;
                    object-fit: cover;
                }
            }
            .re_txt {
                width: 195px;
                font-size: $small_up_font_size;
                font-family: $font_noto_sans;
                margin-bottom: 2px;
            }
        }
    }
    .product_info_n_buy_btn_area {
        width: 405px;
        .product_name {
            margin-top: 25px;
            @include sub_title;
        }
        .rating_n_sns {
            display: flex;
            align-items: center;
            margin: 20px 0;
            justify-content: space-between;
            .rating_count {
                font-size: $small_up_font_size;
                font-family: $font_noto_sans;
                .count_point {
                    display: inline-block;
                    letter-spacing: -1px;
                    transform: translateY(-1px);
                    margin-right: 5px;
                    color: $sub_color_free_speach_blue;
                }
                .current_rating {
                    font-weight: 700;
                }
            }
            .sns_list {
                overflow: hidden;
                li {
                    float: left;
                    font-size: 24px;
                    margin-right: 15px;
                    cursor: pointer;
                    &.like {
                        position: relative;
                        color: red;
                        margin-right: 30px;
                        &::after {
                            content: "";
                            position: absolute;
                            top:5px; left: 39px;
                            width: 2px; height: 24px;
                            background-color: #bbbbbb;
                        }
                    }
                }
            }
        }
        .price {
            overflow: hidden;
            p {
                margin-bottom: 5px;
                &.product_money {
                    @include sub_title;
                }
                &.color_gray {
                    font-size: $small_up_font_size;
                }
            }
        }
    }
    .shipping_n_installment {
        margin: 15px 0 30px;
        padding: 15px 0;
        border-top: 1px solid #bbbbbb;
        border-bottom: 1px solid #bbbbbb;
        .shipping_info, .installment_info {
            overflow: hidden;
            li {
                font-size: $small_up_font_size;
                letter-spacing: -0.5px;
                margin-bottom: 5px;
            }
            .list_left {
                float: left;
            }
            .list_right {
                float: right;
            }
        }
        .shipping_info {
            p {
                text-align: right;
                margin-bottom: 2px;
                span {
                    font-family: $font_noto_sans;
                    color: $sub_color_free_speach_blue;
                }
            }
        }
    }
    .cloth_color_select {
        .color_gray {
            font-size: $defalut_font_size;
            font-family: $font_noto_sans;
            margin-bottom: 5px;
        }
        .select_list {
            display: flex;
            margin-bottom: 30px;
            li {
                margin-right: 15px;
                transition: all 0.2s;
                @include background_cover;
                &:hover{
                    outline: 1px solid $point_color_amber;
                }
                a {
                    @include item_size(90px, 90px);
                }
            }
        }
    }
    .size_select {
        .color_gray {
            font-size: $defalut_font_size;
            font-family: $font_noto_sans;
            margin-bottom: 5px;
        }
        .size_list {
            overflow: hidden;
            text-align: center;
            li {
                float: left;
                @include item_size(69px, 60px);
                line-height: 60px;
                margin-right: 15px;
                background-color: gray;
                transition: all 0.2s;
                a {
                    display: block;
                    font-size: $small_up_font_size;
                }
                &:last-child {
                    margin-right: 0;
                }
            }
        }
    }
    .point {
        margin: 15px 0;
        overflow: hidden;
        p {
            font-size: $small_up_font_size;
            font-family: $font_noto_sans;
        }
        .point_left {
            float: left;
        }
        .point_right {
            float: right;
            color: #2DB400;
        }
    }
    .size_n_buy {
        .size_chk_btn {
            height: 60px;
            text-align: center;
            line-height: 60px;
            border: 2px solid #bbbbbb;
            background-color: #fff;
            margin-bottom: 15px;
            transition: all 0.3s;
            cursor: pointer;
            &:hover{
                border: 2px solid black;
            }
            .size_btn_ment {
                font-family: $font_noto_sans;
                font-size: $defalut_font_size;
                font-weight: 700;
                i {
                    font-size: 20px;
                    margin-right: 15px;
                }
            }
        }
        .buy_n_keep {
            display: flex;
