@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+KR:wght@300;500;700&family=Permanent+Marker&display=swap');

/************************* color_plan ***************************/
$point_color_amber : #ffbf00;
$point_color_black : #000;
$main_color_flora_white : #FFFDF6;
$sub_color_free_speach_blue : #4136BF;
$color_white : #fff;

/************************* font_plan ***************************/
@mixin main_title() {
    font-size: 2.4rem;
    font-weight: 700;
}

@mixin sub_title() {
    font-size: 2rem;
    font-weight: 700;
}

$defalut_font_size : 1.6rem;
$small_font_size : 1.2rem;
$small_up_font_size : 1.4rem;

$font_noto_sans : 'Noto Sans KR',
sans-serif;
$font_perment_marker : 'Permanent Marker';

.color_gray {
    color: rgb(119, 119, 119);
}

/************************* gap＆padding ***************************/
@mixin gap($value) {
    margin: $value;
}

@mixin padding($value) {
    padding: $value;
}

/************************* background standard  ***************************/
@mixin background_contain {
    background-size: contain;
    background-repeat: no-repeat;
}

@mixin background_cover {
    background-size: cover;
    background-repeat: no-repeat;
}

/************************* item_size ***************************/
@mixin item_size ($width, $height) {
    width: $width;
    height: $height;
}

/************************* text_array ***************************/
@mixin text_array ($height) {
    text-align: center;
    line-height: $height;
}

/************************* center array  ***************************/
@mixin box_array {
    width: 1245px;
    margin: 0 auto;
}

/************************* defalut_settings ***************************/
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

li {
    list-style: none;
}

a {
    display: block;
    text-decoration: none;
    color: inherit;
}

html {
    font-size: 10px;
    background-color: $main_color_flora_white;
}

img {
    width: 100%;
    vertical-align: bottom;
}

.sub_title {
    @include sub_title;
    @include text_array(20px);
    margin: 20px 0;
    font-family: $font_perment_marker;
}

.btn {
    position: absolute;
    top: 50%;
    font-size: 50px;
    transform: translateY(-25%);
    z-index: 9999;
    cursor: pointer;
    transition: all 0.2s;
    user-select: none;

    &:hover {
        color: #ffbf00 !important;
    }
}

button {
    border: none;
    outline: none;
    cursor: pointer;
}

label {
    user-select: none;
}

@mixin blue_btn($font_size) {
    background-color: $sub_color_free_speach_blue;
    color: white;
    font-size: $font_size;
}

/********************** control_class ************************/
.none_on {
    display: none !important;
}

.block_on {
    display: block !important;
}

.full_down_on {
    width: 100%;
    height: 400px !important;
}

.visual_page_on {
    font-weight: 700;
}

.hover_on {
    opacity: 1 !important;
}

label {
    display: block;
    cursor: pointer;
}

select {
    outline: none;
}

input {
    display: none;
}

.label_style {
    font-size: $small_up_font_size;
}

.clicked {
    color: black !important;
}

.bgc_amber {
    background-color: $point_color_amber !important;
    color: white;
    font-weight: 700;
}


/************************* contents_product_buy ***************************/
.contents_product_buy {
    display: flex;
    @include box_array;
    margin-top: 60px;

    .product_view_area {
        width: 510px;
        margin-right: 225px;

        .style_code {
            overflow: hidden;
            margin-bottom: 15px;

            p {
                float: left;
                font-size: $defalut_font_size;
                font-family: $font_noto_sans;

                &:first-child {
                    margin-right: 15px;
                }

                &.code_number {
                    font-weight: 700;
                }
            }
        }

        .view_thumnail {
            position: relative;
            @include item_size(100%, 520px);
            overflow: hidden;
            margin-bottom: 15px;
            background-color: #f5f5f5;

            >div {
                @include item_size(100%, 520px);
                background-repeat: no-repeat;
            }

            &:hover {
                cursor: crosshair;
            }

            .view_img01 {
                position: absolute;
                top: 0;
                left: 0;
                opacity: 1;
                transition: all 0.2s;
                background-image: url(../dist/images/detail_page_img/CA23SSJAJL00358002_thumnail/CA23SSJAJL00358002-01.jpg);

                &:hover {
                    opacity: 0;
                }
            }

            .view_img02 {
                background-image: url(../dist/images/detail_page_img/CA23SSJAJL00358002_thumnail/CA23SSJAJL00358002-01_hover.jpg);
            }
        }

        .view_small_thumnail {
            display: flex;
            margin-bottom: 75px;

            .img_box {
                @include item_size(90px, 90px);
                margin-right: 15px;
                overflow: hidden;
                cursor: pointer;
                transition: all 0.2s;

                &:hover {
                    outline: 1px solid $sub_color_free_speach_blue;
                }

                img {
                    width: 100%;
                }
            }

            .src_img {
                display: none;
                @include item_size(90px, 90px);
            }

            .img_box05 {
                margin-right: 0;
            }
        }

        .product_related_area {
            .related_list {
                display: flex;
                margin-bottom: 15px;
            }
        }

        .recently_viewed_product_area {
            margin-bottom: 60px;

            .recently_list {
                display: flex;
            }
        }

        .title_text {
            @include sub_title;
            margin-bottom: 5px;
        }

        .small_product_img {
            margin-right: 15px;

            &:last-child {
                margin-right: 0;
            }

            .re_img_box {
                @include item_size(195px, 195px);
                overflow: hidden;

                img {
                    width: 100%;
                    height: 100%;
                    object-fit: cover;
                }
            }

            .re_txt {
                width: 195px;
                font-size: $small_up_font_size;
                font-family: $font_noto_sans;
                margin-bottom: 2px;
            }
        }
    }

    .product_info_n_buy_btn_area {
        width: 405px;

        .product_name {
            margin-top: 25px;
            @include sub_title;
        }

        .rating_n_sns {
            display: flex;
            align-items: center;
            margin: 20px 0;
            justify-content: space-between;

            .rating_count {
                font-size: $small_up_font_size;
                font-family: $font_noto_sans;

                .count_point {
                    display: inline-block;
                    letter-spacing: -1px;
                    transform: translateY(-1px);
                    margin-right: 5px;
                    color: $sub_color_free_speach_blue;
                }

                .current_rating {
                    font-weight: 700;
                }
            }

            .sns_list {
                overflow: hidden;

                li {
                    float: left;
                    font-size: 24px;
                    margin-right: 15px;
                    cursor: pointer;

                    &.like {
                        position: relative;
                        color: red;
                        margin-right: 30px;

                        &::after {
                            content: "";
                            position: absolute;
                            top: 5px;
                            left: 39px;
                            width: 2px;
                            height: 24px;
                            background-color: #bbbbbb;
                        }
                    }
                }
            }
        }

        .price {
            overflow: hidden;

            p {
                margin-bottom: 5px;

                &.product_money {
                    @include sub_title;
                }

                &.color_gray {
                    font-size: $small_up_font_size;
                }
            }
        }
    }

    .shipping_n_installment {
        margin: 15px 0 30px;
        padding: 15px 0;
        border-top: 1px solid #bbbbbb;
        border-bottom: 1px solid #bbbbbb;

        .shipping_info,
        .installment_info {
            overflow: hidden;

            li {
                font-size: $small_up_font_size;
                letter-spacing: -0.5px;
                margin-bottom: 5px;
            }

            .list_left {
                float: left;
            }

            .list_right {
                float: right;
            }
        }

        .shipping_info {
            p {
                text-align: right;
                margin-bottom: 2px;

                span {
                    font-family: $font_noto_sans;
                    color: $sub_color_free_speach_blue;
                }
            }
        }
    }

    .cloth_color_select {
        .color_gray {
            font-size: $defalut_font_size;
            font-family: $font_noto_sans;
            margin-bottom: 5px;
        }

        .select_list {
            display: flex;
            margin-bottom: 30px;

            li {
                margin-right: 15px;
                transition: all 0.2s;
                @include background_cover;

                &:hover {
                    outline: 1px solid $point_color_amber;
                }

                a {
                    @include item_size(90px, 90px);
                }
            }
        }
    }

    .size_select {
        .color_gray {
            font-size: $defalut_font_size;
            font-family: $font_noto_sans;
            margin-bottom: 5px;
        }

        .size_list {
            overflow: hidden;
            text-align: center;

            li {
                float: left;
                @include item_size(69px, 60px);
                line-height: 60px;
                margin-right: 15px;
                background-color: gray;
                transition: all 0.2s;

                a {
                    display: block;
                    font-size: $small_up_font_size;
                }

                &:last-child {
                    margin-right: 0;
                }
            }
        }
    }

    .point {
        margin: 15px 0;
        overflow: hidden;

        p {
            font-size: $small_up_font_size;
            font-family: $font_noto_sans;
        }

        .point_left {
            float: left;
        }

        .point_right {
            float: right;
            color: #2DB400;
        }
    }

    .size_n_buy {
        .size_chk_btn {
            height: 60px;
            text-align: center;
            line-height: 60px;
            border: 2px solid #bbbbbb;
            background-color: #fff;
            margin-bottom: 15px;
            transition: all 0.3s;
            cursor: pointer;

            &:hover {
                border: 2px solid black;
            }

            .size_btn_ment {
                font-family: $font_noto_sans;
                font-size: $defalut_font_size;
                font-weight: 700;

                i {
                    font-size: 20px;
                    margin-right: 15px;
                }
            }
        }

        .buy_n_keep {
            display: flex;
            text-align: center;
            margin-bottom: 15px;

            >div {
                line-height: 60px;
                cursor: pointer;
                font-size: $small_up_font_size;
                font-weight: 700;
                letter-spacing: -0.4px;
            }

            .keep_btn {
                @include item_size(105px, 60px);
                margin-right: 15px;
                background-color: #fff;
                border: 2px solid #bbbbbb;
            }

            .buy_btn {
                @include item_size(300px, 60px);
                color: #fff;
                background-color: $sub_color_free_speach_blue;
                transition: all 0.2s;

                &:hover {
                    background-color: $point_color_amber;
                }
            }
        }

        .notice {
            font-size: $small_font_size;
            letter-spacing: -0.8px;
        }

        .size_chk_modal_ex {
            display: none;
            position: fixed;
            top: 0;
            left: 0;
            @include item_size(100%, 100%);
            background-color: rgba(0, 0, 0, 0.2);
            z-index: 9999;

            .size_chk_modal {
                position: absolute;
                top: 50%;
                left: 50%;
                transform: translate(-50%, -50%);
                @include item_size(645px, 700px);
                background-color: rgb(131, 131, 131);

                .sub_title {
                    margin: 30px 0;
                }

                .size_chk_modal_inner {
                    margin: 0 auto;
                    @include item_size(465px, 550px);
                    background-color: white;

                    .check_title {
                        font-size: $defalut_font_size;
                        font-family: $font_perment_marker;
                        font-weight: 700;
                        text-align: center;
                        padding: 10px 0;
                    }

                    .size_chk_box_area {
                        .input_area {
                            .input_in_top {
                                display: flex;
                                justify-content: center;

                                >div {
                                    margin-right: 15px;

                                    &:last-child {
                                        margin-right: 0;
                                    }

                                    label {
                                        font-size: $small_up_font_size;
                                        font-family: $font_noto_sans;

                                        span {
                                            color: gray;
                                            font-weight: 700;
                                            margin-left: 2px;
                                        }
                                    }

                                    input {
                                        display: block !important;
                                        outline: none;
                                        @include item_size(175px, 30px);
                                        border: 1px solid black;
                                        margin-top: 5px;
                                        padding: 0 5px;
                                    }
                                }
                            }

                            .input_in_bottom {
                                .siez_search_btn {
                                    display: block;
                                    @include item_size(365px, 40px);
                                    margin: 15px auto 0;
                                    background-color: rgb(51, 51, 51);
                                    color: white;
                                    font-family: $font_noto_sans;
                                    font-size: $small_up_font_size;
                                    transition: all 0.2s;

                                    &:hover {
                                        background-color: rgb(134, 134, 134);
                                    }
                                }
                            }

                            .notice_ment {
                                display: none;
                                margin-top: 15px;
                                font-size: $small_font_size;
                                text-align: center;
                                color: red;
                            }
                        }

                        .size_show_area {
                            margin-top: 20px;
                            text-align: center;

                            .your_size {
                                font-size: $defalut_font_size;
                                font-family: $font_noto_sans;
                            }

                            .show_box {
                                background-color: rgb(197, 197, 197);
                                border-radius: 25px;
                                height: 40px;
                                @include item_size(110px, 40px);
                                margin: 15px auto 20px;

                                .show_txt {
                                    line-height: 40px;
                                    font-size: 20px;
                                    font-weight: 700;
                                    font-family: $font_perment_marker;
                                }
                            }

                            .color_gray {
                                font-size: $small_font_size;
                            }

                            .size_list_chart {
                                width: 405px;
                                margin: 20px auto 0;

                                ul {
                                    display: flex;
                                    border: 1px solid gray;
                                    border-bottom: none;

                                    li {
                                        width: 20%;
                                        font-family: $font_noto_sans;
                                        font-size: $defalut_font_size;
                                        border-right: 1px solid gray;

                                        &:last-child {
                                            border-right: none;
                                        }
                                    }

                                    &.size_list_category {
                                        background-color: rgb(179, 179, 179);
                                    }

                                    &:last-child {
                                        border-bottom: 1px solid gray;
                                    }
                                }
                            }
                        }
                    }
                }
            }

            .close_btn {
                position: absolute;
                top: 8%;
                right: 32%;

                i {
                    display: inline-block;
                    font-size: 40px;
                    background-color: #fff;
                    border-radius: 50%;
                    padding: 0 6px;
                    cursor: pointer;
                }
            }
        }
    }
}

/************************* info_container ***************************/
.info_container {
    @include box_array;

    #product_reviews_section {
        .product_reviews_area {
            .top {
                display: flex;
                align-items: center;
                justify-content: space-between;
            }

            .best_review_btn {
                @include item_size(210px, 40px);
            }

            #review_form {
                .review_list {
                    margin: 20px 0;

                    li {
                        display: flex;
                        align-items: center;
                        justify-content: space-between;

                        .review_ment {
                            width: 65%;
                            margin-left: 15px;

                            .delete {
                                margin-left: 15px;
                                cursor: pointer;
                            }
                        }

                        .right_info {
                            display: flex;

                            span {
                                margin-right: 50px;
                            }

                            .rating_star {
                                color: $point_color_amber;
                                letter-spacing: -0.8px;
                            }
                        }
                    }

                    .not_ment {
                        font-family: $font_noto_sans;
                        text-align: center;
                        font-size: 30px;
                        background-color: #fff;
                        height: 200px;
                        line-height: 200px;
                    }
                }

                .create_btn {
                    margin-left: auto;
                    @include item_size(105px, 40px);
                }

                .review_create {
                    display: none;
                    margin-top: 20px;

                    .text_top_area {
                        display: flex;
                        align-items: center;
                        margin-bottom: 15px;

                        .str_length {
                            font-family: $font_noto_sans;
                            font-size: $small_up_font_size;
                        }

                        .review_rating_star {
                            display: flex;
                            margin-left: 15px;
                            align-items: center;
                            background-color: #fff;
                            border: 2px solid black;
                            padding: 5px 10px;
                            font-size: 20px;
                            color: $sub_color_free_speach_blue;

                            li {
                                margin-right: 5px;
                                cursor: pointer;

                                &:last-child {
                                    margin-right: 0;
                                }
                            }
                        }
                    }

                    #review_text_box {
                        width: 100%;
                        padding: 20px;
                        background-color: #fff;
                        outline: none;
                        border: 1px solid #bbbbbb;
                        resize: none;
                    }

                    #create_complete {
                        display: block;
                        text-align: center;
                        @include item_size(195px, 40px);
                        line-height: 40px;
                        margin-top: 20px;
                        margin-left: auto;
                    }
                }
            }


        }
    }

    #qna_section {
        .qna_notice {
            margin-bottom: 10px;

            .guide_01 {
                text-align: center;
                font-family: $font_noto_sans;
                font-size: 30px;
                font-weight: 700;
            }

            .guide_02 {
                font-size: 20px;
                font-family: $font_noto_sans;
                text-align: center;
                color: rgb(119, 119, 119);
                margin-bottom: 15px;

                span {
                    color: $sub_color_free_speach_blue;
                    font-weight: 700;
                }
            }

            .question_btn {
                @include item_size(195px, 60px);
                margin: 0 auto;
                border: 3px solid $sub_color_free_speach_blue;

                a {
                    @include sub_title;
                    text-align: center;
                    color: $sub_color_free_speach_blue;
                    line-height: 60px;
                }
            }
        }

        #qna_form {
            .create_question {
                display: none;
                margin-bottom: 30px;

                .create_question_top {
                    display: flex;
                    align-items: center;

                    label {
                        font-size: $defalut_font_size;
                        font-family: $font_noto_sans;
                        margin-right: 10px;
                    }

                    #qna_user_id {
                        padding: 5px 10px;
                    }
                }

                .str_length {
                    margin: 15px 0;
                    font-family: $font_noto_sans;
                    font-size: $small_up_font_size;
                }

                #create_question_ment {
                    width: 100%;
                    padding: 20px;
                    resize: none;
                    outline: none;
                    border: 1px solid #bbbbbb;
                }

                #qna_create {
                    margin-top: 15px;
                }

                input {
                    display: block;
                    outline: none;
                    border: 1px solid #bbbbbb;
                }
            }

            .qna {
                .question_list {
                    padding: 0;

                    .question_box {
                        overflow: hidden;
                        padding: 30px 0;
                        user-select: none;

                        .left_box {
                            display: flex;
                            width: 70%;
                            float: left;

                            span {
                                margin-left: 15px;
                            }

                            p {
                                margin-left: 40px;
                                text-align: center;
                            }
                        }

                        .right_box {
                            width: 30%;
                            float: right;

                            span {
                                width: 33.3333%;
                                margin-right: 50px;
                                &.comment_click_on {
                                    margin-right: 0;
                                }
                            }

                            i {
                                font-size: 20px;
                                cursor: pointer;

                            }
                        }

                        .delete {
                            margin-left: 15px;
                            cursor: pointer;
                        }
                    }

                    .answer_box {
                        display: none;

                        .ment_input {
                            >h2 {
                                font-size: $defalut_font_size;
                                font-family: $font_noto_sans;
                                margin: 10px 0;
                            }

                            .answer_text_box {
                                width: 100%;
                                padding: 10px;
                                resize: none;
                                outline: none;
                                border: 1px solid #bbbbbb;
                            }

                            .answer_create {
                                display: block;
                                @include item_size(210px, 40px);
                                margin: 15px 0 20px;
                                margin-left: auto;
                            }
                        }
                        .ment_area {
                            display: flex;
                            align-items: center;
                            padding: 20px 0;
                            font-size: $defalut_font_size;
                            border-top: 1px solid #bbbbbb;
                            .answer {
                                margin-left: 15px;
                                font-size: $small_up_font_size;
                            }
                            .qna_guide_ment {
                                width: 90%;
                                margin-left: 50px;
                                font-size: $small_up_font_size;
                                .spot {
                                    font-size: 1.6rem;
                                    font-family: "Noto Sans KR", sans-serif;
                                }
                            }
                        }
                    }
                }

                .qna_not_ment {
                    font-family: $font_noto_sans;
                    text-align: center;
                    font-size: 30px;
                    background-color: #fff;
                    height: 200px;
                    line-height: 200px;
                }
            }
        }

        .total_info {
            margin-bottom: 30px;
        }
    }

    .info_tab_list {
        display: flex;
        margin: 30px 0;

        li {
            @include item_size(20%, 50px);
            background-color: #dddddd;
            border: 1px solid gray;
            border-right: none;

            &.tab_on {
                background-color: $point_color_amber;
            }

            &:last-child {
                border-right: 1px solid gray;
            }

            a {
                text-align: center;
                font-size: 20px;
                line-height: 50px;
                font-family: $font_noto_sans;
                font-weight: 500;
            }
        }
    }

    .total_info {
        font-size: $defalut_font_size;
    }

    .list_area {
        border-top: 2px solid #000;
        border-bottom: 2px solid #000;

        li {
            padding: 30px 0;
            border-bottom: 1px solid darkgray;
            font-size: $small_up_font_size;

            &:last-child {
                border: none;
            }

            .date {
                font-size: $small_font_size;
                color: rgb(54, 54, 54);
            }
        }
    }

    .pagenation {
        margin: 15px 0;

        .page_list {
            display: flex;
            justify-content: center;

            li {
                color: white;
                margin-right: 10px;
                font-size: $defalut_font_size;
                background-color: gray;
                @include item_size(30px, 30px);
                line-height: 30px;
                cursor: pointer;
                user-select: none;
                text-align: center;

                &:last-child {
                    margin-right: 0;
                }

                &.page_on {
                    background-color: black;
                }
            }
        }
    }

    .ment_box {
        margin-bottom: 20px;

        p {
            font-size: $small_font_size;
            letter-spacing: -0.5px;
            margin-bottom: 5px;
        }

        .color_gray {
            font-size: $small_font_size;
        }
    }

    .more {
        display: inline-block;
        font-size: $small_up_font_size;
        font-weight: 700;
        border-bottom: 1px solid #000;
        margin-bottom: 15px;

        span {
            margin-right: 5px;
        }
    }

    .blue_btn {
        text-align: center;
        @include blue_btn($small_up_font_size);
        cursor: pointer;
        line-height: 40px;
        transition: all 0.2s;

        &:hover {
            background-color: $point_color_amber;
        }
    }
}
