@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+KR:wght@300;500;700&family=Permanent+Marker&display=swap');

/************************* color_plan ***************************/
$point_color_amber : #ffbf00;
$point_color_black : #000;
$main_color_flora_white : #FFFDF6;
$sub_color_free_speach_blue : #4136BF;
$color_white : #fff;

/************************* font_plan ***************************/
@mixin main_title() {
    font-size: 2.4rem;
    font-weight: 700;
}
@mixin sub_title() {
    font-size: 2rem;
    font-weight: 700;
}
$defalut_font_size : 1.6rem;
$small_font_size : 1.2rem;
$small_up_font_size : 1.4rem;

$font_noto_sans : 'Noto Sans KR', sans-serif;
$font_perment_marker : 'Permanent Marker';

/************************* gapï¼†padding ***************************/
@mixin gap($value) {
    margin: $value;
}
@mixin padding($value) {
    padding: $value;
}

/************************* background standard  ***************************/
@mixin background_contain {
    background-size: contain;
    background-repeat: no-repeat;
}
@mixin background_cover {
    background-size: cover;
    background-repeat: no-repeat;
}

/************************* item_size ***************************/
@mixin item_size ($width, $height) {
    width: $width;
    height: $height;
}

/************************* text_array ***************************/
@mixin text_array ($height) {
    text-align: center;
    line-height: $height;
}

/************************* center array  ***************************/
@mixin box_array {
    width: 1245px;
    margin: 0 auto;
}

/************************* defalut_settings ***************************/
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}
li {
    list-style: none;
}
a {
    text-decoration: none;
    color: inherit;
}
html {
    font-size: 10px;
    background-color: $main_color_flora_white;
}
img {
    width: 100%;
    vertical-align: bottom;
}
.sub_title {
    @include sub_title;
    @include text_array(20px);
    margin: 20px 0;
    font-family: $font_perment_marker;
}
.btn {
    position: absolute;
    top: 50%; 
    font-size: 50px;
    transform: translateY(-25%);
    z-index: 9999;
    cursor: pointer;
    transition: all 0.2s;
    user-select: none;
    &:hover{
        color: #ffbf00 !important;
    }
}
/********************** control_class ************************/
.block_on {
    display: block !important;
}
.full_down_on {
    width: 100%;
    height: 400px !important;
}
.visual_page_on {
    font-weight: 700;
}
/************************* header_ex ***************************/
.header_ex {
    color: $color_white;
    background-color: $point_color_black;
    .header {
        position: relative;
        @include box_array;
        .logo {
            padding: 10px 0;
            a {
                display: block;
                @include item_size(175px, 50px);
                margin: 0 auto;
                background-image: url(../dist/images/main_logo.png);
                @include background_contain;
            }
            
        }
        .gnb {
            position: absolute;
            padding: 10px 0;
            top: 10px; right: 0;
            .gnb_list {
                overflow: hidden;
                li {
                    float: left;
                    margin-right: 15px;
                    &.icon {
                        margin-left: 10px;
                        a {
                            width: 20px; height: 20px;
                            background-image: url(../dist/images/search_icon.png);
                            @include background_cover;
                        }
                    }
                    &.cart {
                        a {
                            width: 30px; height: 30px;
                            background-image: url(../dist/images/cart_icon.png);
                            @include background_cover;
                            transform: translateY(-5px);
                        }
                    }
                    &:last-child {
                        margin-right: 0;
                    }
                    a {
                        display: block;
                        font-size: $small_font_size;
                        font-family: $font_noto_sans;
                    }
                }
            }
        }
    }
}

/************************* lnb_ex ***************************/
.lnb_ex_design {
    background-color: $point_color_black;
    position: relative;
    .lnb {
        display: flex;
        @include box_array;
        height: 50px;
        justify-content: space-between;
        align-items: center;
        a {
            display: block;
        }
        .main_menu_list {
            display: flex;
            .menu_list, .new_list {    
                text-align: center;
                position: relative;
                transition: all 0.1s;
                &:hover {
                    >a {
                        background-color: #4136BF;
                    }
                }
                >a {
                    padding: 0 30px;
                    color: $color_white;
                    font-size: 20px;
                    line-height: 50px;
                    font-weight: 700;
                    letter-spacing: -0.8px;
                }
                .li_inner_ul {
                    display: none;
                    position: absolute;
                    text-align: center;
                    width: 100%;
                    transition: all 0.3s;
                    top: 55px;
                    z-index: 10;
                    >li {
                        padding: 5px 0;
                        >a {
                            font-family: $font_noto_sans;
                            font-size: $small_up_font_size;
                            &:hover {
                                color: #4136BF;
                            }
                        }

                    }
                }
            }
        }
        .real_time_keyward_frame {
            position: relative;
            @include item_size(120px, 20px);
            overflow: hidden;
            .keyward_list {
                position: absolute;
                top: 0;
                overflow: hidden;
                &.keyward_list_active {
                    transition: all 1s;
