/************************* product_container ***************************/
.product_container {
    @include box_array;
    position: relative;
    .product {
        position: relative;
        @include item_size(1245px, 340px);
        margin-bottom: 40px;
        .product_frame_design { 
            position: relative;
            @include item_size(1245px, 340px);
            overflow: hidden;
            /* transition: all 0.6s; */
            .product_list {
                display: flex;
                position: absolute;
                left: 0;
                @include item_size(6285px, 300px);
                transform: translateX(-2520px);
                li {
                    &:last-child {
                        margin-right: 0;
                    }
                    margin-right: 15px;
                    @include item_size(300px, 300px);
                    a {
                        position: relative;
                        display: block;
                        @include item_size(300px, 300px);
                        &::after {
                            content: "";
                            position: absolute;
                            z-index: 1;
                            top: 0; left: 0;
                            @include item_size(300px, 300px);
                            opacity: 0;
                            transition: all 0.3s;
                        }
                        &:hover {
                            span {
                                opacity: 1;
                            }
                            &::after {
                                opacity: 10;
                                background-color: rgba(0,0,0,0.2);
                            }
                        }
                        img {
                            @include item_size(100%, 100%);
                        }
                        span {
                            position: absolute;
                            display: block;
                            opacity: 0;
                            width: 100%;
                            text-align: center;
                            z-index: 10;
                            font-size: $small_font_size;
                            color: $color_white;
                            &:nth-child(2) {
                                top: 120px;
                                left: 50%;
                                transform: translateX(-50%);
                            }
                            &:nth-child(3) {
                                top: 140px;
                                left: 50%;
                                transform: translateX(-50%);
                            }
                        }
                    }
                }
                &.move_active {
                    transition: all 0.6s;
                }
            }
        }
        #prev_btn01,#prev_btn02 {
            left: -80px;
        }
        #next_btn01,#next_btn02 {
            right: -80px;
        }
    }
}

