@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+KR:wght@300;500;700&family=Permanent+Marker&display=swap');

/************************* color_plan ***************************/
$point_color_amber : #ffbf00;
$point_color_black : #000;
$main_color_flora_white : #FFFDF6;
$sub_color_free_speach_blue : #4136BF;
$color_white : #fff;

/************************* font_plan ***************************/
@mixin main_title() {
    font-size: 2.4rem;
    font-weight: 700;
}

@mixin sub_title() {
    font-size: 2rem;
    font-weight: 700;
}

$defalut_font_size : 1.6rem;
$small_font_size : 1.2rem;
$small_up_font_size : 1.4rem;

$font_noto_sans : 'Noto Sans KR',
sans-serif;
$font_perment_marker : 'Permanent Marker';

/************************* gap＆padding ***************************/
@mixin gap($value) {
    margin: $value;
}

@mixin padding($value) {
    padding: $value;
}

/************************* background standard  ***************************/
@mixin background_contain {
    background-size: contain;
    background-repeat: no-repeat;
}

@mixin background_cover {
    background-size: cover;
    background-repeat: no-repeat;
}

/************************* item_size ***************************/
@mixin item_size ($width, $height) {
    width: $width;
    height: $height;
}

/************************* text_array ***************************/
@mixin text_array ($height) {
    text-align: center;
    line-height: $height;
}

/************************* center array  ***************************/
@mixin box_array {
    width: 1245px;
    margin: 0 auto;
}

/************************* defalut_settings ***************************/
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

li {
    list-style: none;
}

a {
    text-decoration: none;
    color: inherit;
}

html {
    font-size: 10px;
    background-color: $main_color_flora_white;
}

img {
    width: 100%;
    vertical-align: bottom;
}

.sub_title {
    @include sub_title;
    @include text_array(20px);
    margin: 20px 0;
    font-family: $font_perment_marker;
}

.btn {
    position: absolute;
    top: 50%;
    font-size: 50px;
    transform: translateY(-25%);
    z-index: 9999;
    cursor: pointer;
    transition: all 0.2s;
    user-select: none;

    &:hover {
        color: #ffbf00 !important;
    }
}

button {
    border: none;
    outline: none;
    cursor: pointer;
}

a {
    display: block;
}

label {
    user-select: none;
}
/********************** control_class ************************/
.none_on {
    display: none !important;
}

.block_on {
    display: block !important;
}

.full_down_on {
    width: 100%;
    height: 400px !important;
}

.visual_page_on {
    font-weight: 700;
}

.hover_on {
    opacity: 1 !important;
}

label {
    display: block;
    cursor: pointer;
}

select {
    outline: none;
}

input {
    display: none;
}
.label_style {
    font-size: $small_up_font_size;
}
.clicked {
    color: black !important; 
}
.clicked_border {
    border: 2px solid $point_color_amber !important;
}

/************************* visual_sub ***************************/
.visual_sub {
    position: relative;
    @include item_size(100%, 400px);
    background-image: url(../dist/images/visual_sub01.jpg);
    @include background_cover;
    background-position-y: -100px;
    margin-bottom: 20px;
    a {
        position: absolute;
        top: 0; left: 0;
        width: 100%; height: 100%;
        z-index: 10;
    }
    .ment_box_ex {
        position: relative;
        @include box_array;
        height: 400px;

        .ment_box {
            position: absolute;
            left: 0px; bottom: 25px;
            width: 800px; height: 120px;
            padding-top: 20px;
            padding-left: 25px;
            background-color: rgba(0, 0, 0, 0.4);
            h2 {
                color: white;
                font-size: 24px;
                font-family: $font_perment_marker;
            }
            p {
                color: white;
                margin-top: 10px;
                font-family: $font_noto_sans;
                font-size: $defalut_font_size;
            }
        }
    }
}


/************************* wrapper ***************************/
.wrapper {
    display: flex;
    @include box_array;
    margin-bottom: 120px;

    .side_filter {
        flex: 0 0 auto;
        width: 315px;

        >div {
            margin-right: 105px;
            padding: 20px 0;
            border-bottom: 1px solid gray;

            &:first-child {
                padding: 0 !important;
            }

            &:last-child {
                border: none;
            }

            .info_title {
                margin-bottom: 10px;
                letter-spacing: -0.2px;
            }
        }

        .menu_list_section {
            .menu_title {
                font-size: 2.4rem;
                padding: 15px 0;
                border-bottom: 4px solid black;
            }

            .product_menu_list {
                font-size: $small_up_font_size;
                font-family: $font_noto_sans;
                font-weight: 700;
                padding: 20px 0;
                label {
                    display: block;
                    user-select: none;
                    margin-bottom: 10px;
                    cursor: pointer;
                    color: gray;
                    transition: all 0.1s;
                    &:last-child {
                        margin-bottom: 0;
                    }
                }
            }
        }

        .color_select {
            div {
                display: flex;

                label {
                    display: block;
                    @include item_size(20px, 20px);
                    cursor: pointer;
                    margin-right: 15px;
                    margin-bottom: 15px;
                    &.black_box {
                        background-color: black;
                    }
                    &.white_box {
                        background-color: white;
                        border: 1px solid #bbbbbb;
                    }
                    &.gray_box {
                        background-color: gray;
                    }
                    &.brown_box {
                        background-color: burlywood;
                    }
                    &.blue_box {
                        background-color: rgb(0, 81, 255);
                    }
