                        background-color: skyblue;
                    }
                    &.green_box {
                        background-color: rgb(9, 163, 9);
                    }
                    &.green_down_box {
                        background-color: rgb(16, 90, 16);
                    }
                    &.orange_box {
                        background-color: orange;
                    }
                    &.pink_box {
                        background-color: pink;
                    }
                    &.purple_box {
                        background-color: rgb(155, 13, 155);
                    }
                }
            }
        }

        .price_select {
            label {
