                    font-size: $small_up_font_size;
                    font-family: $font_noto_sans;
                }
                .price {
                    display: inline-block;
                    font-size: 18px;
                    font-family: $font_noto_sans;
                    margin-bottom: 60px;
                }
                .price_unit {
                    font-size: 20px;
                    margin-right: 2px;
                }
                .best, .new {
                    display: inline-block;
                    margin-top: 6px;
                    margin-right: 5px;
                    font-size: $small_up_font_size;
                    font-weight: 700;
                }
                .best {
                    color: #8e2720;
                    animation: best 0.8s infinite;
                }
                .new {
                    color: #4136BF;
                    animation: new 0.8s 0.4s infinite;
                }
                @keyframes best {
                    50% {
                        color: #d88f8a;
                    }
                    100% {
                        color: #b92d23;
                    }
                }
                @keyframes new {
                    50% {
                        color: #938beb;
                    }
                    100% {
                        color: #4136BF;
                    }
                }
            }
        }
    }
}
.pagenation {
    overflow: hidden;
    @include box_array;
    display: flex;
    margin-bottom: 70px;
    text-align: center;
    justify-content: center;
    ul {
        display: flex;
        margin-right: 20px;
    }
    li {
        margin-right: 20px;
        font-size: 16px;
        border-radius: 50%;
        @include item_size(30px, 30px);
        line-height: 30px;
        font-family: $font_noto_sans;
        transition: all 0.2s;
        cursor: pointer;
        &:last-child {
            margin-right: 0;
        }
        &.page_on {
            color: white;
            background-color: #4136BF;
            border-radius: 50%;
            font-weight: 700;
        }
    }
    .page_btn {
        transform: translateY(-3px);
        margin-right: 20px;
        font-size: 26px;
        cursor: pointer;
    }
}
